import React, { useState, useEffect } from "react";
import { motion } from "framer-motion";

const img1 = `${process.env.PUBLIC_URL}/assets/img1.png`;
const img2 = `${process.env.PUBLIC_URL}/assets/img2.png`;
const img3 = `${process.env.PUBLIC_URL}/assets/img3.png`;
export default function App() {
  const tshirts = [
    { id: 1, src: img1 },
    { id: 2, src: img2 },
    { id: 3, src: img3 },
  ];

  const [currentIndex, setCurrentIndex] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentIndex((prevIndex) => (prevIndex + 1) % tshirts.length);
    }, 3000);

    return () => clearInterval(interval);
  }, [tshirts.length]);

  return (
    <div className="app">
      <header className="navbar">
        <div className="logo">Tee Design Hub</div>
        <nav>
          <ul>
            <li className="headerOptions">Home</li>
            <li className="headerOptions">About</li>
            <li className="headerOptions">Products</li>
            <li className="headerOptions">Contact</li>
            <li className="headerOptions">Shop</li>
          </ul>
        </nav>
      </header>
      <main className="hero-section">
        <div className="text-content">
          <h1>
            Design <br /> Your <br /> Dream T-
          </h1>
          <p>
            Choose from stylish presets or create your own by uploading designs,
            adding text, or graphics.
          </p>
          <button className="get-started">Get Started</button>
        </div>
        <div className="tshirt-carousel">
          {tshirts.map((tshirt, index) => (
            <motion.div
              className={`tshirt-display ${index === currentIndex ? 'front' : ''}`}
              key={tshirt.id}
              initial={{ opacity: 0, scale: 0.8 }}
              animate={{ opacity: index === currentIndex ? 1 : 0.3, scale: index === currentIndex ? 1 : 0.8 }}
              transition={{ duration: 1 }}
            >
              <img
                src={tshirt.src}
                alt={`T-shirt ${index + 1}`}
                className="tshirt"
              />
            </motion.div>
          ))}
        </div>
      </main>
    </div>
  );
}
