import React, { useState } from "react";
import ProductDetails from "./Proddetails";
// Individual Card Component
function Card({ imgSrc, title, price, onClick }) {
  return (
    <div className="card" onClick={onClick}>
      <img src={imgSrc} alt={title} className="card-image" />
      <div className="card-content">
        <h3 className="card-title">{title}</h3>
        <p className="card-price">${price}</p>
        <button className="card-button">View Details</button>
      </div>
    </div>
  );
}



// Gallery Component to Display Multiple Cards
function Gallery() {
  const [selectedProduct, setSelectedProduct] = useState(null);

  const items = [
    { imgSrc: "/assets/img1.png", title: "Product 1", price: 29.99 },
    { imgSrc: "/assets/img2.png", title: "Product 2", price: 49.99 },
    { imgSrc: "/assets/img3.png", title: "Product 3", price: 19.99 },
    { imgSrc: "/assets/img4.png", title: "Product 4", price: 99.99 },
  ];

  return (
    <div className="gallery-container">
      <div className="gallery">
        {items.map((item, index) => (
          <Card
            key={index}
            imgSrc={item.imgSrc}
            title={item.title}
            price={item.price}
            onClick={() => setSelectedProduct(item)}
          />
        ))}
      </div>

      {/* Render Product Details if a product is selected */}
      {selectedProduct && (
        <ProductDetails
          product={selectedProduct}
          onClose={() => setSelectedProduct(null)}
        />
      )}
    </div>
  );
}

export default Gallery;
