import React, { useState } from "react";
import { motion } from "framer-motion";

export default function App() {
  const [dropdownVisible, setDropdownVisible] = useState(false);

  const handleHover = () => setDropdownVisible(true);
  const handleLeave = () => setDropdownVisible(false);

  return (
    <div className="app">
      <header className="navbar">
        <div className="logo">Tee Design Hub</div>
        <nav>
          <ul>
            <li className="headerOptions">Home</li>
            <li className="headerOptions">About</li>
            <li className="headerOptions">Products</li>
            <li className="headerOptions">Contact</li>
            <li className="headerOptions">Cart</li>
            <li
              className="headerOptions account-icon"
              onMouseEnter={handleHover}
              onMouseLeave={handleLeave}
            >
              <i className="fas fa-user-circle"></i> {/* Accounts Icon */}
              {dropdownVisible && (
                <div className="dropdown-menu">
                  <ul>
                    <li>Orders</li>
                    <li>Logout</li>
                  </ul>
                </div>
              )}
            </li>
          </ul>
        </nav>
      </header>

      <main className="hero-section">
        <div className="text-content">
          <h1>
            Design <br /> Your <br /> Dream T-Shirts
          </h1>
          <p>
            Choose from stylish presets or create your own by uploading designs,
            adding text, or graphics.
          </p>
          <button className="get-started">Get Started</button>
        </div>
      </main>
    </div>
  );
}
