import React, { useState, useEffect } from "react";
import Header from "./Header.jsx";
import Register from "./Register.jsx";
import Login from "./Login.jsx";
import Prod from "./Prod.jsx";

export default function App() {
  const [showRegister, setShowRegister] = useState(false);
  const [showLogin, setShowLogin] = useState(false);
  const [isHeaderVisible, setHeaderVisible] = useState(true);
  const [lastScrollPos, setLastScrollPos] = useState(0);

  const handleGetStarted = () => {
    setShowRegister(true);
    setShowLogin(false);
    document.body.classList.add("body-blurred");
  };

  const handleShowLogin = () => {
    setShowLogin(true);
    setShowRegister(false);
    document.body.classList.add("body-blurred");
  };

  const closeRegisterOrLogin = () => {
    setShowRegister(false);
    setShowLogin(false);
    document.body.classList.remove("body-blurred");
  };

  // Scroll event handler
  const handleScroll = () => {
    const currentScrollPos = window.scrollY;
    if (currentScrollPos > lastScrollPos && currentScrollPos > 100) {
      setHeaderVisible(false); // Hide header on scroll down
    } else {
      setHeaderVisible(true); // Show header on scroll up
    }
    setLastScrollPos(currentScrollPos);
  };

  // Add event listener for scroll
  useEffect(() => {
    window.addEventListener("scroll", handleScroll);
    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, [lastScrollPos]);

  return (
    <div className="app">
      {/* Header Section */}
      <div className={`header-container ${isHeaderVisible ? "visible" : "hidden"}`}>
        <Header />
      </div>

      {/* Hero Section */}
      <main className="hero-section first-page">
        <div className="text-content">
          <h1>
            Design <br /> Your <br /> Dream T-Shirts
          </h1>
          <p>
            Choose from stylish presets or create your own by uploading designs,
            adding text, or graphics.
          </p>
          <button className="get-started" onClick={handleGetStarted}>
            Get Started
          </button>
        </div>
      </main>

      {/* Page Break */}
      <div className="page-break rest-pages"></div>

      {/* Products Section */}
      <section className="products-section">
        <h2>Our Products</h2>
        <div className="products">
          <Prod />
          <Prod />
        </div>
      </section>

      {/* Overlay for Register/Login */}
      {(showRegister || showLogin) && (
        <div className="overlay">
          {showRegister && <Register onClose={closeRegisterOrLogin} />}
          {showLogin && <Login onClose={closeRegisterOrLogin} />}
        </div>
      )}
    </div>
  );
}
