import React, { useState, useEffect } from "react";
import { motion } from "framer-motion";

import img1 from ".../public/assets/Boys_Reflective_Cartoon_Graphic_Top-removebg-preview.png";
import img2 from ".../public/assets/download-removebg-preview-O1tG9PqTt-transformed.png";
import img3 from ".../public/assets/T-_SHIRT-removebg-preview-transformed.png";
export default function App() {
  const tshirts = [
    { id: 1, src: img1 },
    { id: 2, src: img2 },
    { id: 3, src: img3 },
  ];

  const [currentIndex, setCurrentIndex] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentIndex((prevIndex) => (prevIndex + 1) % tshirts.length);
    }, 3000);

    return () => clearInterval(interval);
  }, [tshirts.length]);

  return (
    <div className="app">
      <header className="navbar">
        <div className="logo">Tee Design Hub</div>
        <nav>
          <ul>
            <li className="headerOptions">Home</li>
            <li className="headerOptions">About</li>
            <li className="headerOptions">Products</li>
            <li className="headerOptions">Contact</li>
            <li className="headerOptions">Shop</li>
          </ul>
        </nav>
      </header>
      <main className="hero-section">
        <div className="text-content">
          <h1>
            Design <br /> Your <br /> Dream T-
          </h1>
          <p>
            Choose from stylish presets or create your own by uploading designs,
            adding text, or graphics.
          </p>
          <button className="get-started">Get Started</button>
        </div>
        <motion.div
          className="tshirt-display"
          initial={{ opacity: 0, scale: 0.8 }}
          animate={{ opacity: 1, scale: 1 }}
          transition={{ duration: 1 }}
          key={currentIndex}
        >
          <img
            src={tshirts[currentIndex].src}
            alt={`Tshirt ${currentIndex + 1}`}
            className="tshirt"
          />
        </motion.div>
      </main>
    </div>
  );
}
