import React from "react";

// Individual Card Component
function Card({ imgSrc, title, price }) {
  return (
    <div className="card shadow-lg rounded-2xl overflow-hidden hover:scale-105 transition-transform duration-300 ease-in-out">
      <img
        src={imgSrc}
        alt={title}
        className="h-48 w-full object-cover"
      />
      <div className="p-4">
        <h3 className="text-lg font-semibold text-gray-800 truncate">{title}</h3>
        <p className="text-gray-600 mt-2">${price}</p>
        <button className="mt-4 w-full py-2 px-4 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors">
          Add to Cart
        </button>
      </div>
    </div>
  );
}

// Gallery Component to Display Multiple Cards
function Gallery() {
  const items = [
    { imgSrc: "/assets/img1.png", title: "Product 1", price: 29.99 },
    { imgSrc: "/assets/img2.png", title: "Product 2", price: 49.99 },
    { imgSrc: "/assets/img3.png", title: "Product 3", price: 19.99 },
    { imgSrc: "/assets/img4.png", title: "Product 4", price: 99.99 },
  ];

  return (
    <div className="grid grid-cols-2 md:grid-cols-4 gap-6 p-6">
      {items.map((item, index) => (
        <Card
          key={index}
          imgSrc={item.imgSrc}
          title={item.title}
          price={item.price}
        />
      ))}
    </div>
  );
}

export default Gallery;
